mapper.delete:
delete from IncrRate

	AND IncrID in
	
		#{item}

mapper.select:
select IncrID from IncrRate

	 and ChangeTime <= #{changeTime} 

order by IncrID limit 0,#{maxRecordCount}

select
IncrID,ChangeID,HotelID,RoomTypeID,RateplanID,HotelCode,StartDate,EndDate,Status,Member,Weekend,MemberCost,WeekendCost,AddBed,ChangeTime,OperateTime,InsertTime,CurrencyCode,PriceID
from IncrRate

	
		 and ChangeTime > #{changeTime} 
	
	
		 and IncrID > #{incrID}  
	

order by IncrID limit 0,#{maxRecordCount}
