mapper.select:
select
IncrID,ChangeID,HotelID,RoomTypeID,RateplanID,HotelCode,StartDate,EndDate,Status,Channel,Member,Weekend,MemberCost,WeekendCost,AddBed,ChangeTime,OperateTime,InsertTime,CurrencyCode,PriceID
from ${subTableName}

	
		 and ChangeTime > #{params.changeTime} 
	
	
		 and ID > #{params.ID}  
	

order by ID limit 0,#{params.maxRecordCount}

select count(*) from ${subTableName}

	 
		and InsertTime <= #{endTime} 
		and InsertTime >= #{startTime}
