mapper.select:
usp_execute_sql_prod 'select * from ( select ROW_NUMBER() OVER(ORDER BY op_date) RowNumber, 1 StateType ,hotel_id HotelId ,NULL HotelCode ,NULL RoomId ,NULL RoomTypeId ,NULL RatePlanId ,case hotel_status when 0 then 1 else 0 end Status ,op_date ChangeTime ,getdate() as InsertTime ,hotel_name as Name from MHotelBase
and op_date >= ''${startTime}''
and op_date <= ''${endTime}''
) t
and RowNumber >= ${startNum}
and RowNumber <= ${endNum}
'

usp_execute_sql_prod 'select * from ( select ROW_NUMBER() OVER(ORDER BY s.op_date) RowNumber, 2 StateType ,ms.MHotelID HotelId ,hotel_id HotelCode ,NULL RoomId ,NULL RoomTypeId ,NULL RatePlanId ,case isreserve when 0 then 1 else 0 end Status ,op_date ChangeTime ,getdate() as InsertTime from Hotel s inner join mhotelshotelrelation ms on s.hotel_id=ms.SHotelID
and s.op_date >= ''${startTime}''
and s.op_date <= ''${endTime}''
) t
and RowNumber >= ${startNum}
and RowNumber <= ${endNum}
'

